# Path Variables

export dev="~/.termux-dev"

# System Variables

export system=$(uname -o)
export arch=$(uname -m)
export show=$(pwd)

# Light Colors

export black="\e[1;30m"
export blue="\e[1;34m"
export green="\e[1;32m"
export cyan="\e[1;36m"
export red="\e[1;31m"
export purple="\e[1;35m"
export yellow="\e[1;33m"
export white="\e[1;37m"

# Dark Colors

export d_black="\e[0;30m"
export d_blue="\e[0;34m"
export d_green="\e[0;32m"
export d_cyan="\e[0;36m"
export d_red="\e[0;31m"
export d_purple="\e[0;35m"
export d_yellow="\e[0;33m"
export d_white="\e[0;37m"

# Background Colors

export bg_black=$(setterm -background black)
export bg_blue=$(setterm -background blue)
export bg_green=$(setterm -background green)
export bg_cyan=$(setterm -background cyan)
export bg_red=$(setterm -background red)
export bg_yellow=$(setterm -background yellow)
export bg_white=$(setterm -background white)

